package java_test_folder;

public class Test_Java {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 5;
		int num2 = 10;
		
		int result = num1 & num2;
		
		System.out.println(result);
		
		num1 = 5;
		num2 = 10;
		
		result = num1 | num2;
		System.out.println(result);
		
		result = num1 ^ num2;
		
		System.out.println(result);
		
		int num = 10;
		result = ~num;
		System.out.println(result);
		
		System.out.println("fucking github");
	}

}
